import React, {Component} from "react";
import employeeList from "../data/EmployeeList";
import "./SearchFilter.css"

export default class SearchFilter extends Component {
    constructor() {
        super();
        this.state = {
            name: "",
            employeeToDisplay: []
        };
    }

    search = event =>{
        const keyword = event.target.value;
        if (keyword !== '') {
          const results = employeeList.filter((employee) => {
            return employee.name.toLowerCase().startsWith(keyword.toLowerCase());
          });
          this.setState({
              name: keyword,
              employeeToDisplay: results
          })
        } else {
            this.setState({
                name: keyword,
                employeeToDisplay: employeeList
            })
        }
        this.setState({
            name: keyword,
        })
      };

    render() {
        return (
            <div className="container">
              <input
                type="search"
                value={this.state.name}
                onChange={this.search}
                className="input"
                placeholder="Filter"
              />
              <div className="user-list">
                {employeeList.length > 0 ? (
                  employeeList.map((employee) => (
                    <li key={employee.id} className="user">
                      <span className="user-id">{employee.id}</span>
                      <span className="user-name">{employee.name}</span>
                      <span className="user-age">{employee.age} year old</span>
                      <span className="user-designation">{employee.designation}</span>
                    </li>
                  ))
                ) : (
                  <h1>No results found!</h1>
                )}
              </div>
            </div>
          );
    }
    // componentDidMount(){
    //     this.search();
    // }
}
